mapper.insert:
INSERT INTO board(

) VALUES (
	  #{id}
	, #{userId}
	, #{title}
	, #{climbing_member}
	, #{climbing_date}
	, #{connection_link}
	, #{content}
	, #{gender}
	, NOW()
	, NULL			
)

mapper.update:
UPDATE board
SET
	  update_time = NOW()
	, id=#{id}
	, userId=#{userId}
	, title=#{title}
	, climbing_member=#{climbing_member}
	, climbing_date=#{climbing_date}
	, connection_link=#{connection_link}
	, content=#{content}
	, gender=#{gender}


WHERE
	id = #{id}
